import { useEffect, useState } from "react";
import Formulario from "./HomeComponents/Formulario";
import Navbar from "./HomeComponents/Navbar";
import Tabla from "./HomeComponents/Table";

/*
import { Button, Typography } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { loginActions } from "../store/storelogin"
import { useNavigate } from "react-router-dom"
import { useEffect } from "react"
*/

export default function Home() {
    
    const [list, setList] = useState([])

    useEffect(() => {
        const initList = []
        fetch(`http://localhost:3030/data`)
            .then(res => res.json())
            .then(json => json.rows.forEach(element => {
                initList.push(element)
            }))
        setTimeout(() => {
            setList(initList)
        },1000)
    },[])

    //Utilizar hook useSelector para obtener datos de store (datos de estado actual)

    /*
    const userData = useSelector(state => state.login)
    console.log(userData)

    const username = userData.username
    const userRol = userData.userRol
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const isLoggedin = userData.isAutenticated
    useEffect(() => {
        if(!isLoggedin) {
            //Volver a la pagina de index
            navigate('/')
        }
    }, [isLoggedin, navigate])

    */

    const update = () =>  {
        const newList = []
        setTimeout(() =>
            fetch(`http://localhost:3030/data`)
                .then(res => res.json())
                .then(json => {
                    json.rows.forEach((row) => {
                        newList.push(row)
                    })
                    setList(newList)
                })
            ,1000
        )
    }

    return (
        <div>
            <Navbar username="zongzhen"/>
            <Formulario event={update}/>
            <Tabla list={list} updateEvent={update}/>
        </div>
    )
}
