import { Button, Typography } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { loginActions } from "../store/storelogin"
import { useNavigate } from "react-router-dom"
import { useEffect } from "react"

export default function Home() {

    const isLoggedin = userData.isAutenticated
    useEffect(() => {
        if(!isLoggedin) {
            //Volver a la pagina de index
            navigate('/')
        }
    }, [isLoggedin, navigate])

    //Utilizar hook useSelector para obtener datos de store (datos de estado actual)
    const userData = useSelector(state => state.login)
    console.log(userData)

    const username = userData.username
    const userRol = userData.userRol
    const dispatch = useDispatch()
    const navigate = useNavigate()

    return (
        <div>
            <h1>Pagina de home</h1>
            <Typography variant="h6">Nombre de usuario: {username} | Rol de usuario: {userRol}</Typography>
            <Button variant="contained" onClick={ () => {
                //Realizar accion de logout
                dispatch(loginActions.logout())
                navigate('/')
            }}>volver</Button>
        </div>
    )
}
