import { useEffect, useState } from "react";
import Formulario from "./HomeComponents/Formulario";
import Navbar from "./HomeComponents/Navbar";
import Tabla from "./HomeComponents/Table";
import { useSelector } from "react-redux"
import { useNavigate } from "react-router-dom"


export default function Home() {
    
    const [list, setList] = useState([])

    useEffect(() => {
        const initList = []
        const initState = false
        const res = fetch(`http://localhost:3030/data`)
            .then(res => res.json())
            .then(json => {
                json.rows.forEach(element => {
                    initList.push(element)
                })
                initState = true
            })
        
        while(!initList){
            console.log('waiting')
        }
        setList(initList)

    },[])

    //Utilizar hook useSelector para obtener datos de store (datos de estado actual)
    const userData = useSelector(state => state.login)
    const navigate = useNavigate()

    const isLoggedin = userData.isAutenticated
    useEffect(() => {
        if(!isLoggedin) {
            navigate('/')
        }
    }, [isLoggedin, navigate])

    

    const update = () =>  {
        const newList = []
        setTimeout(() =>
            fetch(`http://localhost:3030/data`)
                .then(res => res.json())
                .then(json => {
                    json.rows.forEach((row) => {
                        newList.push(row)
                    })
                    setList(newList)
                })
            ,1000
        )
    }

    return (
        <div>
            <Navbar username={userData.username}/>
            <Formulario event={update}/>
            <Tabla list={list} updateEvent={update}/>
        </div>
    )
}
